import localforage from 'localforage';
class AppStorage {
    constructor({ driver }) {
        this.keys = {
            userState: 'us',
        };
        this.getUserState = () => {
            return this.lf.getItem(this.keys.userState);
        };
        this.lf = localforage.createInstance({
            driver,
            description: 'MyReact Storage Instance',
            name: 'myreact',
            storeName: 'myreactdb',
            version: 1,
        });
    }
}
export default AppStorage;
